import React from "react";

function ShipperHomepage() {
    // Sample data for the orders
    const orders = [
        {
            orderId: "ORD001",
            books: [
                { title: "Book A", quantity: 2 },
                { title: "Book B", quantity: 1 },
            ],
            address: {
                street: "888 Joyful Street",
                ward: "Brilliant Ward",
                district: "Magnificent District",
                city: "Significant City",
                country: "Happy Country",
            },
            receiverName: "John Doe",
            contactNumber: "123-456-7890",
        },
        {
            orderId: "ORD002",
            books: [
                { title: "Book C", quantity: 3 },
                { title: "Book D", quantity: 2 },
            ],
            address: {
                street: "456 Wonderful Avenue",
                ward: "Cheerful Ward",
                district: "Glorious District",
                city: "Prominent City",
                country: "Joyful Country",
            },
            receiverName: "Jane Smith",
            contactNumber: "987-654-3210",
        },
        {
            orderId: "ORD003",
            books: [
                { title: "Book E", quantity: 5 },
            ],
            address: {
                street: "789 Peaceful Road",
                ward: "Harmonious Ward",
                district: "Spectacular District",
                city: "Remarkable City",
                country: "Blissful Country",
            },
            receiverName: "Alice Johnson",
            contactNumber: "555-123-4567",
        },
    ];

    return (
        <div
            className="shipper-homepage"
            style={{
                display: "flex",
                flexDirection: "column",
                justifyContent: "center",
                alignItems: "center",
                minHeight: "100vh",
                backgroundColor: "#f9f9f9",
            }}
        >
            <h1 style={{ textAlign: "center", marginBottom: "20px" }}>
                Shipper Homepage
            </h1>
            <p style={{ textAlign: "center", marginBottom: "20px" }}>
                Below is your shipping orders:
            </p>

            {/* Table */}
            <div style={{ overflowX: "auto", width: "90%", maxWidth: "1000px" }}>
                <table
                    className="orders-table"
                    border="1"
                    style={{
                        width: "100%",
                        textAlign: "left",
                        borderCollapse: "collapse",
                        backgroundColor: "#ffffff",
                    }}
                >
                    <thead>
                        <tr>
                            <th>Order ID</th>
                            <th>List of Books and Quantities</th>
                            <th>Address</th>
                            <th>Receiver Name</th>
                            <th>Contact Number</th>
                        </tr>
                    </thead>
                    <tbody>
                        {orders.map((order, index) => (
                            <tr key={index}>
                                <td>{order.orderId}</td>
                                <td>
                                    {order.books.map((book, bookIndex) => (
                                        <div key={bookIndex}>
                                            {book.title} (x{book.quantity})
                                        </div>
                                    ))}
                                </td>
                                <td>
                                    {`${order.address.street}, ${order.address.ward}, ${order.address.district}, ${order.address.city}, ${order.address.country}`}
                                </td>
                                <td>{order.receiverName}</td>
                                <td>{order.contactNumber}</td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            </div>
        </div>
    );
}

export default ShipperHomepage;
