import React from "react";
import { Link } from "react-router-dom";
import '../styles/Sidebar.css'
import nebulaLogo from '../assets/NebulaLogo.png'

function Sidebar() {
    const menuItems = [
      { label: "Home", icon: "üè†" },
      { label: "Exploring", icon: "üîç" },
      { label: "Wishlist", icon: "‚ù§Ô∏è" },
      { label: "Cart", icon: "üõí" },
      { label: "Profile", icon: "üë§" },
      { label: "Purchase History", icon: "üìú" },
      { label: "Settings", icon: "‚öôÔ∏è" },
    ];

    return (
      <div className="sidebar">
        {/* Wrap the logo in a Link */}
        <div className="logo">
          <Link to="/">
            <img src={nebulaLogo} alt="Nebula Bookstore Logo" />
          </Link>
        </div>
        <ul className="nav flex-column mb-auto">
          {menuItems.map((item, index) => (
            <li className="nav-item" key={index}>
              <a href="/login" className="nav-link text-dark">
                {item.icon} {item.label}
              </a>
            </li>
          ))}
        </ul>
        <button className="btn btn-primary mt-auto">Login</button>
      </div>
    );
  }
export default Sidebar;
